import React, { useState, useEffect } from 'react';
import { Search, Filter, MapPin, Clock, Mountain, Star, Users, Eye, Zap, TrendingUp, AlertCircle, RefreshCw, Heart, Activity, Award } from 'lucide-react';

const InteractiveTrailSelector = () => {
  const [trails, setTrails] = useState([]);
  const [filteredTrails, setFilteredTrails] = useState([]);
  const [selectedTrails, setSelectedTrails] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [recommendationMetrics, setRecommendationMetrics] = useState(null);
  const [loading, setLoading] = useState(false);
  const [dataLoading, setDataLoading] = useState(true);
  const [error, setError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('checking');

  const [groupMembers, setGroupMembers] = useState([
    { name: "Alice", preferences: ["mountain", "lake", "waterfall"] },
    { name: "Bob", preferences: ["ocean", "beach", "lake"] },
    { name: "Carol", preferences: ["forest", "waterfall", "lake"] }
  ]);

  // API Configuration
  const API_BASE_URL = 'http://localhost:3001/api';

  const [filters, setFilters] = useState({
    difficulty: '',
    maxDistance: '',
    maxTime: '',
    maxElevation: '',
    trailType: '',
    sceneryTypes: [],
    minRating: ''
  });

  const [searchTerm, setSearchTerm] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [showRecommendations, setShowRecommendations] = useState(false);

  // Check backend health status
  const checkHealth = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        const healthData = await response.json();
        setConnectionStatus('connected');
        setError(null);
        return healthData;
      } else {
        throw new Error(`Health check failed: ${response.status}`);
      }
    } catch (err) {
      setConnectionStatus('disconnected');
      setError(`Backend connection failed: ${err.message}`);
      throw err;
    }
  };

  // Load trail data
  const loadTrails = async (retryCount = 0) => {
    setDataLoading(true);
    setError(null);

    try {
      // Check backend health status
      await checkHealth();

      // Fetch trail data
      const response = await fetch(`${API_BASE_URL}/trails`);

      if (!response.ok) {
        throw new Error(`Failed to fetch trails: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();

      // Validate and clean data
      const validTrails = data.filter(trail => {
        return trail && 
               trail.trail_id && 
               trail.trail_name && 
               typeof trail.distance_km === 'number' &&
               !isNaN(trail.distance_km) &&
               trail.scenery_types;
      }).map(trail => ({
        ...trail,
        // Ensure scenery_types is an array
        scenery_types: Array.isArray(trail.scenery_types) 
          ? trail.scenery_types 
          : (typeof trail.scenery_types === 'string' 
              ? trail.scenery_types.split(',').map(s => s.trim()) 
              : [])
      }));

      setTrails(validTrails);
      setFilteredTrails(validTrails);
      setConnectionStatus('connected');

      console.log(`✅ Successfully loaded ${validTrails.length} trails`);

    } catch (error) {
      console.error('❌ Error loading trails:', error);
      setError(error.message);
      setConnectionStatus('disconnected');

      if (retryCount < 3) {
        setTimeout(() => loadTrails(retryCount + 1), 2000);
      }
    } finally {
      setDataLoading(false);
    }
  };

  // Get all unique scenery types
  const getAllSceneryTypes = () => {
    const allTypes = trails.flatMap(trail => trail.scenery_types);
    return [...new Set(allTypes)];
  };

  // Apply filter conditions
  useEffect(() => {
    let filtered = trails.filter(trail => {
      // Search term filter
      if (searchTerm && !trail.trail_name.toLowerCase().includes(searchTerm.toLowerCase()) &&
          !trail.location.toLowerCase().includes(searchTerm.toLowerCase())) {
        return false;
      }

      // Difficulty filter
      if (filters.difficulty && trail.difficulty !== filters.difficulty) {
        return false;
      }

      // Distance filter
      if (filters.maxDistance && trail.distance_km > parseFloat(filters.maxDistance)) {
        return false;
      }

      // Time filter
      if (filters.maxTime && trail.estimated_time_hours > parseFloat(filters.maxTime)) {
        return false;
      }

      // Elevation filter
      if (filters.maxElevation && trail.elevation_gain_m > parseInt(filters.maxElevation)) {
        return false;
      }

      // Trail type filter
      if (filters.trailType && trail.trail_type !== filters.trailType) {
        return false;
      }

      // Scenery type filter
      if (filters.sceneryTypes.length > 0) {
        const hasMatchingScenery = filters.sceneryTypes.some(selectedType =>
          trail.scenery_types.some(trailType => trailType.includes(selectedType))
        );
        if (!hasMatchingScenery) {
          return false;
        }
      }

      // Rating filter
      if (filters.minRating && trail.rating < parseFloat(filters.minRating)) {
        return false;
      }

      return true;
    });

    setFilteredTrails(filtered);
  }, [trails, filters, searchTerm]);

  // Handle filter changes
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // Handle scenery type selection
  const handleSceneryTypeToggle = (sceneryType) => {
    setFilters(prev => ({
      ...prev,
      sceneryTypes: prev.sceneryTypes.includes(sceneryType)
        ? prev.sceneryTypes.filter(type => type !== sceneryType)
        : [...prev.sceneryTypes, sceneryType]
    }));
  };

  // Add trail to selection list
  const handleTrailSelect = (trail) => {
    if (selectedTrails.find(t => t.trail_id === trail.trail_id)) {
      setSelectedTrails(prev => prev.filter(t => t.trail_id !== trail.trail_id));
    } else {
      setSelectedTrails(prev => [...prev, trail]);
    }
  };

  // Calculate group match percentage
  const calculateGroupMatch = (trail) => {
    let matchCount = 0;
    groupMembers.forEach(member => {
      const memberMatch = member.preferences.some(pref =>
        trail.scenery_types.some(scenery => 
          scenery.toLowerCase().includes(pref.toLowerCase())
        )
      );
      if (memberMatch) matchCount++;
    });
    return (matchCount / groupMembers.length) * 100;
  };

  // Get recommended trails
  const getRecommendations = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/trails/recommend`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          groupPreferences: groupMembers,
          filters: filters,
          k: 5
        })
      });

      if (response.ok) {
        const data = await response.json();
        setRecommendations(data.recommendations);
        setRecommendationMetrics(data.metrics);
        setShowRecommendations(true);
      } else {
        console.error('Failed to get recommendations');
      }
    } catch (error) {
      console.error('Error getting recommendations:', error);
    } finally {
      setLoading(false);
    }
  };

  // Clear all filters
  const clearFilters = () => {
    setFilters({
      difficulty: '',
      maxDistance: '',
      maxTime: '',
      maxElevation: '',
      trailType: '',
      sceneryTypes: [],
      minRating: ''
    });
    setSearchTerm('');
  };

  // Initial load
  useEffect(() => {
    loadTrails();
  }, []);

  // Loading state
  if (dataLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="animate-spin mx-auto mb-4 text-blue-600" size={48} />
          <h2 className="text-xl font-semibold text-gray-800">Loading Trail Data...</h2>
          <p className="text-gray-600">Connecting to backend and loading trails</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto p-6">
        {/* Error Alert */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <AlertCircle className="text-red-500" size={20} />
                <div>
                  <h3 className="font-medium text-red-800">Connection Issue</h3>
                  <p className="text-sm text-red-600">{error}</p>
                </div>
              </div>
              <button 
                onClick={() => loadTrails()} 
                className="px-3 py-1 bg-red-100 text-red-700 rounded hover:bg-red-200 transition-colors"
              >
                Retry
              </button>
            </div>
          </div>
        )}

        {/* 头部区域 */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex items-center gap-3 mb-2">
            <Mountain className="text-blue-500" size={24} />
            <h1 className="text-2xl font-bold text-gray-800">Feature-Based Trail Selection System</h1>
          </div>
          <p className="text-gray-600">
            Manually select the most suitable hiking trails based on group preferences and trail features.
          </p>
        </div>

        {/* 群体成员显示 */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <Users size={20} />
            Group Member Preferences
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {groupMembers.map((member, index) => (
              <div key={index} className="bg-gray-50 rounded-lg p-4">
                <h4 className="font-medium text-gray-800 mb-3">{member.name}</h4>
                <div className="flex flex-wrap gap-2">
                  {member.preferences.map((pref, i) => (
                    <span key={i} className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
                      {pref}
                    </span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* 搜索和筛选控件 */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex flex-col gap-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search trail name or location..."
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <div className="flex gap-3">
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="flex-1 bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
              >
                <Filter size={20} />
                {showFilters ? 'Hide Filters' : 'Show Filters'}
              </button>
              <button
                onClick={getRecommendations}
                disabled={loading || connectionStatus !== 'connected'}
                className="flex-1 bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 transition-colors flex items-center justify-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <RefreshCw className="animate-spin" size={20} />
                ) : (
                  <Zap size={20} />
                )}
                {loading ? 'Getting Recommendations...' : 'Get AI Recommendations'}
              </button>
            </div>

            <button
              onClick={clearFilters}
              className="w-full bg-gray-500 text-white py-3 px-4 rounded-lg hover:bg-gray-600 transition-colors"
            >
              Clear Filters
            </button>
          </div>

          {/* 筛选面板 */}
          {showFilters && (
            <div className="mt-6 pt-6 border-t border-gray-200">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Difficulty</label>
                  <select
                    value={filters.difficulty}
                    onChange={(e) => handleFilterChange('difficulty', e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">All Difficulties</option>
                    <option value="Easy">Easy</option>
                    <option value="Moderate">Moderate</option>
                    <option value="Hard">Hard</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Max Distance (km)</label>
                  <input
                    type="number"
                    value={filters.maxDistance}
                    onChange={(e) => handleFilterChange('maxDistance', e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="e.g. 10"
                    min="0"
                    step="0.1"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Max Time (hours)</label>
                  <input
                    type="number"
                    value={filters.maxTime}
                    onChange={(e) => handleFilterChange('maxTime', e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="e.g. 4"
                    min="0"
                    step="0.1"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Max Elevation (m)</label>
                  <input
                    type="number"
                    value={filters.maxElevation}
                    onChange={(e) => handleFilterChange('maxElevation', e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="e.g. 500"
                    min="0"
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Trail Type</label>
                  <select
                    value={filters.trailType}
                    onChange={(e) => handleFilterChange('trailType', e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">All Types</option>
                    <option value="Loop">Loop</option>
                    <option value="Out & Back">Out & Back</option>
                    <option value="Point-to-Point">Point-to-Point</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Min Rating</label>
                  <select
                    value={filters.minRating}
                    onChange={(e) => handleFilterChange('minRating', e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">Any Rating</option>
                    <option value="4.0">4.0+</option>
                    <option value="4.2">4.2+</option>
                    <option value="4.4">4.4+</option>
                    <option value="4.6">4.6+</option>
                  </select>
                </div>
              </div>

              {/* Scenery Type Filters */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Scenery Features</label>
                <div className="flex flex-wrap gap-2 max-h-32 overflow-y-auto">
                  {getAllSceneryTypes().slice(0, 20).map((sceneryType, index) => (
                    <button
                      key={index}
                      onClick={() => handleSceneryTypeToggle(sceneryType)}
                      className={`px-3 py-1 rounded-full text-sm transition-colors ${
                        filters.sceneryTypes.includes(sceneryType)
                          ? 'bg-blue-600 text-white'
                          : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                      }`}
                    >
                      {sceneryType}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* 结果统计 */}
          <div className="flex justify-between items-center mt-4 pt-4 border-t border-gray-200">
            <span className="text-gray-600">
              Found <span className="font-semibold text-blue-600">{filteredTrails.length}</span> trails
            </span>
            <span className="text-gray-600">
              Selected <span className="font-semibold text-green-600">{selectedTrails.length}</span> trails
            </span>
          </div>
        </div>

        {/* AI Recommendations */}
        {showRecommendations && recommendations.length > 0 && (
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <h2 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
              <TrendingUp className="text-green-600" />
              AI Recommendations ({recommendations.length})
            </h2>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-semibold text-gray-800 mb-3">Recommended Trails</h3>
                <div className="space-y-4">
                  {recommendations.map((trail, index) => (
                    <div key={trail.trail_id} className="border border-green-200 rounded-lg p-4 bg-green-50">
                      <div className="flex justify-between items-start mb-2">
                        <div>
                          <h4 className="font-semibold text-gray-800">
                            {index + 1}. {trail.trail_name}
                          </h4>
                          <p className="text-sm text-gray-600">{trail.location}</p>
                        </div>
                        <div className="flex items-center gap-1">
                          <Star className="text-yellow-500" size={16} />
                          <span className="text-sm text-gray-600">{trail.rating}</span>
                        </div>
                      </div>

                      <div className="text-sm text-gray-600 mb-2">
                        {trail.difficulty} • {trail.distance_km}km • {trail.estimated_time_hours}h • {trail.elevation_gain_m}m↗
                      </div>

                      <div className="flex flex-wrap gap-1 mb-2">
                        {trail.scenery_types.map((scenery, i) => (
                          <span key={i} className="px-2 py-1 bg-green-100 text-green-800 rounded-full text-xs">
                            {scenery}
                          </span>
                        ))}
                      </div>

                      <div className="text-sm">
                        Group Match: <span className="font-medium text-green-600">{trail.groupMatchPercentage}%</span>
                        <br />
                        Avg Member Utility: <span className="font-medium text-blue-600">{(trail.averageMemberUtility * 100).toFixed(0)}%</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div>
                <h3 className="font-semibold text-gray-800 mb-3">Recommendation Metrics</h3>
                {recommendationMetrics && (
                  <div className="space-y-3">
                    <div className="bg-blue-50 rounded-lg p-3">
                      <h4 className="font-medium text-blue-800 mb-2">Overall Statistics</h4>
                      <div className="text-sm text-blue-700 space-y-1">
                        <p>Average Rating: {recommendationMetrics.averageRating.toFixed(1)}/5.0</p>
                        <p>Average Distance: {recommendationMetrics.averageDistance.toFixed(1)}km</p>
                        <p>Average Time: {recommendationMetrics.averageTime.toFixed(1)} hours</p>
                        <p>Average Elevation: {recommendationMetrics.averageElevation.toFixed(0)}m</p>
                      </div>
                    </div>

                    <div className="bg-green-50 rounded-lg p-3">
                      <h4 className="font-medium text-green-800 mb-2">Algorithm Performance</h4>
                      <div className="text-sm text-green-700 space-y-1">
                        <p>Diversity Score: {(recommendationMetrics.diversityScore * 100).toFixed(0)}%</p>
                        <p>Regret Score: {recommendationMetrics.regretScore.toFixed(2)}</p>
                      </div>
                    </div>

                    <div className="bg-purple-50 rounded-lg p-3">
                      <h4 className="font-medium text-purple-800 mb-2">Group Satisfaction</h4>
                      <div className="text-sm text-purple-700 space-y-1">
                        {groupMembers.map((member, index) => (
                          <p key={member.name}>
                            {member.name}: {recommendationMetrics.groupMatchPercentages[index]}% match
                          </p>
                        ))}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* 步道列表 */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
            <Eye className="text-blue-600" />
            Available Trails ({filteredTrails.length})
          </h2>
          <div className="space-y-4 max-h-96 overflow-y-auto">
            {filteredTrails.slice(0, 20).map((trail) => {
              const isSelected = selectedTrails.find(t => t.trail_id === trail.trail_id);
              const groupMatch = calculateGroupMatch(trail);

              return (
                <div
                  key={trail.trail_id}
                  className={`border rounded-lg p-4 transition-all cursor-pointer ${
                    isSelected 
                      ? 'border-green-500 bg-green-50' 
                      : 'border-gray-200 hover:border-blue-300 hover:bg-blue-50'
                  }`}
                  onClick={() => handleTrailSelect(trail)}
                >
                  <div className="flex justify-between items-start mb-2">
                    <h3 className="font-semibold text-gray-800">{trail.trail_name}</h3>
                    <div className="flex items-center gap-1">
                      <Star className="text-yellow-500" size={16} />
                      <span className="text-sm text-gray-600">{trail.rating}</span>
                    </div>
                  </div>

                  <div className="flex items-center gap-4 text-sm text-gray-600 mb-2">
                    <span className="flex items-center gap-1">
                      <MapPin size={14} />
                      {trail.location}
                    </span>
                    <span className="flex items-center gap-1">
                      <Clock size={14} />
                      {trail.estimated_time_hours}h
                    </span>
                  </div>

                  <div className="flex justify-between items-center mb-2">
                    <div className="flex gap-2 text-sm">
                      <span className={`px-2 py-1 rounded ${
                        trail.difficulty === 'Easy' ? 'bg-green-100 text-green-800' :
                        trail.difficulty === 'Moderate' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {trail.difficulty}
                      </span>
                      <span className="px-2 py-1 bg-blue-100 text-blue-800 rounded">
                        {trail.trail_type}
                      </span>
                    </div>
                    <div className="text-sm text-gray-600">
                      {trail.distance_km}km • {trail.elevation_gain_m}m↗
                    </div>
                  </div>

                  <div className="flex flex-wrap gap-1 mb-2">
                    {trail.scenery_types.map((scenery, index) => (
                      <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 rounded-full text-xs">
                        {scenery}
                      </span>
                    ))}
                  </div>

                  <div className="flex justify-between items-center">
                    <div className="text-sm text-gray-500">
                      Group Match: <span className="font-medium text-blue-600">{groupMatch.toFixed(0)}%</span>
                    </div>
                    <button
                      className={`px-3 py-1 rounded text-sm font-medium ${
                        isSelected 
                          ? 'bg-green-600 text-white' 
                          : 'bg-blue-600 text-white hover:bg-blue-700'
                      }`}
                    >
                      {isSelected ? 'Selected' : 'Select'}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default InteractiveTrailSelector;
