import React, { useState, useEffect } from 'react';
import { Search, Filter, MapPin, Clock, Mountain, Star, Users, Eye, Zap, TrendingUp, AlertCircle, RefreshCw, Heart, Activity, Award, ChevronDown, X, Plus, User, Settings } from 'lucide-react';

const InteractiveTrailSelector = () => {
  const [trails, setTrails] = useState([]);
  const [filteredTrails, setFilteredTrails] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [recommendationMetrics, setRecommendationMetrics] = useState(null);
  const [loading, setLoading] = useState(false);
  const [dataLoading, setDataLoading] = useState(false);
  const [error, setError] = useState(null);

  // Group management
  const [groupMembers, setGroupMembers] = useState([]);
  const [currentStep, setCurrentStep] = useState('group'); // 'group', 'preferences', 'results'
  const [editingMember, setEditingMember] = useState(null);

  // Available preferences
  const availablePreferences = [
    'mountain', 'lake', 'waterfall', 'ocean', 'beach', 'forest', 
    'alpine', 'glacier', 'city', 'canyon', 'river', 'meadow'
  ];

  const [filters, setFilters] = useState({
    difficulty: '',
    maxDistance: '',
    maxTime: '',
    minRating: ''
  });

  const [searchTerm, setSearchTerm] = useState('');
  const [showFilters, setShowFilters] = useState(false);

  // Load trail data from backend
  useEffect(() => {
    const loadTrails = async () => {
      setDataLoading(true);
      setError(null);

      try {
        const response = await fetch('http://localhost:3001/api/trails');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setTrails(data);
        setFilteredTrails(data);
      } catch (err) {
        console.error('Error loading trails:', err);
        setError(`Failed to load trails: ${err.message}`);
      } finally {
        setDataLoading(false);
      }
    };

    loadTrails();
  }, []);

  // Apply filters
  useEffect(() => {
    let filtered = trails.filter(trail => {
      if (searchTerm && !trail.trail_name.toLowerCase().includes(searchTerm.toLowerCase()) &&
          !trail.location.toLowerCase().includes(searchTerm.toLowerCase())) {
        return false;
      }

      if (filters.difficulty && trail.difficulty !== filters.difficulty) {
        return false;
      }

      if (filters.maxDistance && trail.distance_km > parseFloat(filters.maxDistance)) {
        return false;
      }

      if (filters.maxTime && trail.estimated_time_hours > parseFloat(filters.maxTime)) {
        return false;
      }

      if (filters.minRating && trail.rating < parseFloat(filters.minRating)) {
        return false;
      }

      return true;
    });

    setFilteredTrails(filtered);
  }, [trails, filters, searchTerm]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const addGroupMember = () => {
    const newMember = {
      id: Date.now(),
      name: `Member ${groupMembers.length + 1}`,
      preferences: []
    };
    setGroupMembers([...groupMembers, newMember]);
  };

  const removeGroupMember = (id) => {
    setGroupMembers(groupMembers.filter(member => member.id !== id));
  };

  const updateMemberName = (id, name) => {
    setGroupMembers(groupMembers.map(member => 
      member.id === id ? { ...member, name } : member
    ));
  };

  const togglePreference = (memberId, preference) => {
    setGroupMembers(groupMembers.map(member => {
      if (member.id === memberId) {
        const newPreferences = member.preferences.includes(preference)
          ? member.preferences.filter(p => p !== preference)
          : [...member.preferences, preference];
        return { ...member, preferences: newPreferences };
      }
      return member;
    }));
  };

  const calculateGroupMatch = (trail) => {
    if (groupMembers.length === 0) return 0;
    let matchCount = 0;
    groupMembers.forEach(member => {
      const memberMatch = member.preferences.some(pref =>
        trail.scenery_types.some(scenery => 
          scenery.toLowerCase().includes(pref.toLowerCase())
        )
      );
      if (memberMatch) matchCount++;
    });
    return (matchCount / groupMembers.length) * 100;
  };

  const getRecommendations = async () => {
    if (groupMembers.length === 0) {
      setError('Please add at least one group member');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/trails/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          groupPreferences: groupMembers,
          filters: filters,
          numTrails: 5
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setRecommendations(data.recommendations);
      setRecommendationMetrics(data.metrics);
      setCurrentStep('results');
    } catch (err) {
      console.error('Error getting recommendations:', err);
      setError(`Failed to get recommendations: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const clearFilters = () => {
    setFilters({
      difficulty: '',
      maxDistance: '',
      maxTime: '',
      minRating: ''
    });
    setSearchTerm('');
  };

  if (dataLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mb-4 mx-auto">
            <Mountain className="text-white" size={32} />
          </div>
          <h2 className="text-xl font-semibold text-gray-800 mb-2">Loading Trail Data...</h2>
          <p className="text-gray-600">Connecting to backend and loading trails</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto p-6">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="w-12 h-12 bg-blue-500 rounded-lg flex items-center justify-center">
              <Mountain className="text-white" size={24} />
            </div>
            <h1 className="text-3xl font-bold text-gray-800">Trail Explorer</h1>
          </div>
          <p className="text-gray-600">Discover your perfect hiking adventure with AI-powered recommendations</p>
        </div>

        {/* Error Alert */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <div className="flex items-center gap-2">
              <AlertCircle className="text-red-500" size={20} />
              <span className="text-red-700">{error}</span>
            </div>
          </div>
        )}

        {/* Stats Overview */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
          <div className="bg-blue-50 rounded-lg p-4 text-center">
            <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Eye className="text-blue-600" size={20} />
            </div>
            <div className="text-2xl font-bold text-gray-800">{filteredTrails.length}</div>
            <div className="text-sm text-gray-600">Available Trails</div>
          </div>

          <div className="bg-green-50 rounded-lg p-4 text-center">
            <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Heart className="text-green-600" size={20} />
            </div>
            <div className="text-2xl font-bold text-gray-800">{recommendations.length}</div>
            <div className="text-sm text-gray-600">Recommended</div>
          </div>

          <div className="bg-purple-50 rounded-lg p-4 text-center">
            <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Users className="text-purple-600" size={20} />
            </div>
            <div className="text-2xl font-bold text-gray-800">{groupMembers.length}</div>
            <div className="text-sm text-gray-600">Group Members</div>
          </div>

          <div className="bg-yellow-50 rounded-lg p-4 text-center">
            <div className="w-8 h-8 bg-yellow-100 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Star className="text-yellow-600" size={20} />
            </div>
            <div className="text-2xl font-bold text-gray-800">4.6</div>
            <div className="text-sm text-gray-600">Avg Rating</div>
          </div>
        </div>

        {/* Step 1: Group Members */}
        {currentStep === 'group' && (
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
              <Users size={20} />
              Group Members
            </h2>

            <div className="space-y-4 mb-6">
              {groupMembers.map((member) => (
                <div key={member.id} className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <User className="text-blue-600" size={20} />
                  </div>
                  <input
                    type="text"
                    value={member.name}
                    onChange={(e) => updateMemberName(member.id, e.target.value)}
                    className="flex-1 p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Member name"
                  />
                  <button
                    onClick={() => removeGroupMember(member.id)}
                    className="p-2 text-red-500 hover:bg-red-50 rounded"
                  >
                    <X size={16} />
                  </button>
                </div>
              ))}
            </div>

            <div className="flex gap-4">
              <button
                onClick={addGroupMember}
                className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
              >
                <Plus size={16} />
                Add Member
              </button>

              {groupMembers.length > 0 && (
                <button
                  onClick={() => setCurrentStep('preferences')}
                  className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
                >
                  Continue to Preferences
                </button>
              )}
            </div>
          </div>
        )}

        {/* Step 2: Preferences */}
        {currentStep === 'preferences' && (
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
              <Settings size={20} />
              Group Preferences
            </h2>

            <div className="space-y-6">
              {groupMembers.map((member) => (
                <div key={member.id} className="p-4 border border-gray-200 rounded-lg">
                  <h3 className="font-medium text-gray-800 mb-3">{member.name}</h3>
                  <div className="flex flex-wrap gap-2">
                    {availablePreferences.map((pref) => (
                      <button
                        key={pref}
                        onClick={() => togglePreference(member.id, pref)}
                        className={`px-3 py-1 rounded-full text-sm transition-colors ${
                          member.preferences.includes(pref)
                            ? 'bg-blue-500 text-white'
                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                        }`}
                      >
                        {pref}
                      </button>
                    ))}
                  </div>
                </div>
              ))}
            </div>

            <div className="flex gap-4 mt-6">
              <button
                onClick={() => setCurrentStep('group')}
                className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
              >
                Back to Group
              </button>

              <button
                onClick={getRecommendations}
                disabled={loading}
                className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors disabled:opacity-50"
              >
                {loading ? 'Getting Recommendations...' : 'Get Recommendations'}
              </button>
            </div>
          </div>
        )}

        {/* Step 3: Results */}
        {currentStep === 'results' && (
          <div className="space-y-6">
            {/* Search and Filters */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex gap-4 mb-4">
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                  <input
                    type="text"
                    placeholder="Search trails..."
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>

                <button
                  onClick={() => setShowFilters(!showFilters)}
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                >
                  <Filter size={16} />
                </button>

                <button
                  onClick={clearFilters}
                  className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
                >
                  Clear
                </button>
              </div>

              {showFilters && (
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 pt-4 border-t border-gray-200">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Difficulty</label>
                    <select
                      value={filters.difficulty}
                      onChange={(e) => handleFilterChange('difficulty', e.target.value)}
                      className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="">All</option>
                      <option value="Easy">Easy</option>
                      <option value="Moderate">Moderate</option>
                      <option value="Hard">Hard</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Max Distance (km)</label>
                    <input
                      type="number"
                      value={filters.maxDistance}
                      onChange={(e) => handleFilterChange('maxDistance', e.target.value)}
                      className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="e.g. 10"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Max Time (hours)</label>
                    <input
                      type="number"
                      value={filters.maxTime}
                      onChange={(e) => handleFilterChange('maxTime', e.target.value)}
                      className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="e.g. 4"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Min Rating</label>
                    <select
                      value={filters.minRating}
                      onChange={(e) => handleFilterChange('minRating', e.target.value)}
                      className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="">Any</option>
                      <option value="4.0">4.0+</option>
                      <option value="4.2">4.2+</option>
                      <option value="4.4">4.4+</option>
                      <option value="4.6">4.6+</option>
                    </select>
                  </div>
                </div>
              )}
            </div>

            {/* Top 5 Recommendations */}
            {recommendations.length > 0 && (
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                  <TrendingUp className="text-green-600" size={20} />
                  Top 5 Recommended Trails
                </h2>

                <div className="space-y-4">
                  {recommendations.map((trail, index) => (
                    <div key={trail.trail_id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                      <div className="flex justify-between items-start mb-2">
                        <div>
                          <div className="flex items-center gap-2 mb-1">
                            <span className="w-6 h-6 bg-green-500 text-white rounded-full flex items-center justify-center text-xs font-bold">
                              {index + 1}
                            </span>
                            <h3 className="font-semibold text-gray-800">{trail.trail_name}</h3>
                          </div>
                          <p className="text-sm text-gray-600 flex items-center gap-1">
                            <MapPin size={14} />
                            {trail.location}
                          </p>
                        </div>
                        <div className="flex items-center gap-1">
                          <Star className="text-yellow-500" size={16} />
                          <span className="text-sm font-medium">{trail.rating}</span>
                        </div>
                      </div>

                      <div className="flex items-center gap-4 text-sm text-gray-600 mb-3">
                        <span className="flex items-center gap-1">
                          <Activity size={14} />
                          {trail.difficulty}
                        </span>
                        <span className="flex items-center gap-1">
                          <MapPin size={14} />
                          {trail.distance_km}km
                        </span>
                        <span className="flex items-center gap-1">
                          <Clock size={14} />
                          {trail.estimated_time_hours}h
                        </span>
                      </div>

                      <div className="flex flex-wrap gap-1 mb-3">
                        {trail.scenery_types.slice(0, 4).map((scenery, i) => (
                          <span key={i} className="px-2 py-1 bg-green-100 text-green-800 rounded-full text-xs">
                            {scenery}
                          </span>
                        ))}
                        {trail.scenery_types.length > 4 && (
                          <span className="px-2 py-1 bg-gray-200 text-gray-600 rounded-full text-xs">
                            +{trail.scenery_types.length - 4} more
                          </span>
                        )}
                      </div>

                      <div className="text-sm text-gray-600">
                        Group Match: <span className="font-medium text-green-600">{calculateGroupMatch(trail).toFixed(0)}%</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* All Available Trails */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                <Eye className="text-blue-600" size={20} />
                All Available Trails ({filteredTrails.length})
              </h2>

              <div className="space-y-4 max-h-96 overflow-y-auto">
                {filteredTrails.slice(0, 20).map((trail) => {
                  const groupMatch = calculateGroupMatch(trail);

                  return (
                    <div key={trail.trail_id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                      <div className="flex justify-between items-start mb-2">
                        <div>
                          <h3 className="font-semibold text-gray-800">{trail.trail_name}</h3>
                          <p className="text-sm text-gray-600 flex items-center gap-1">
                            <MapPin size={14} />
                            {trail.location}
                          </p>
                        </div>
                        <div className="flex items-center gap-1">
                          <Star className="text-yellow-500" size={16} />
                          <span className="text-sm font-medium">{trail.rating}</span>
                        </div>
                      </div>

                      <div className="flex items-center gap-4 text-sm text-gray-600 mb-3">
                        <span className="flex items-center gap-1">
                          <Activity size={14} />
                          {trail.difficulty}
                        </span>
                        <span className="flex items-center gap-1">
                          <MapPin size={14} />
                          {trail.distance_km}km
                        </span>
                        <span className="flex items-center gap-1">
                          <Clock size={14} />
                          {trail.estimated_time_hours}h
                        </span>
                      </div>

                      <div className="flex flex-wrap gap-1 mb-3">
                        {trail.scenery_types.slice(0, 3).map((scenery, index) => (
                          <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 rounded-full text-xs">
                            {scenery}
                          </span>
                        ))}
                        {trail.scenery_types.length > 3 && (
                          <span className="px-2 py-1 bg-gray-200 text-gray-600 rounded-full text-xs">
                            +{trail.scenery_types.length - 3} more
                          </span>
                        )}
                      </div>

                      <div className="text-sm text-gray-600">
                        Group Match: <span className="font-medium text-blue-600">{groupMatch.toFixed(0)}%</span>
                      </div>
                    </div>
                  );
                })}
              </div>

              {filteredTrails.length === 0 && (
                <div className="text-center py-8">
                  <Search className="text-gray-400 mx-auto mb-2" size={32} />
                  <p className="text-gray-600">No trails found</p>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Navigation */}
        {currentStep !== 'group' && (
          <div className="flex justify-center gap-4 mt-8">
            <button
              onClick={() => setCurrentStep('group')}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
            >
              Start Over
            </button>

            {currentStep === 'results' && (
              <button
                onClick={() => setCurrentStep('preferences')}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
              >
                Edit Preferences
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default InteractiveTrailSelector;
