import React, { useState, useEffect } from 'react';
import { Search, Filter, MapPin, Clock, Mountain, Star, Users, Eye, Zap, TrendingUp, AlertCircle, RefreshCw } from 'lucide-react';

const InteractiveTrailSelector = () => {
  const [trails, setTrails] = useState([]);
  const [filteredTrails, setFilteredTrails] = useState([]);
  const [selectedTrails, setSelectedTrails] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [recommendationMetrics, setRecommendationMetrics] = useState(null);
  const [loading, setLoading] = useState(false);
  const [dataLoading, setDataLoading] = useState(true);
  const [error, setError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('checking');

  const [groupMembers, setGroupMembers] = useState([
    { name: "Alice", preferences: ["mountain", "lake", "waterfall"] },
    { name: "Bob", preferences: ["ocean", "beach", "lake"] },
    { name: "Carol", preferences: ["forest", "waterfall", "lake"] }
  ]);

  // APIé…ç½®
  const API_BASE_URL = 'http://localhost:3001/api';

  const [filters, setFilters] = useState({
    difficulty: '',
    maxDistance: '',
    maxTime: '',
    maxElevation: '',
    trailType: '',
    sceneryTypes: [],
    minRating: ''
  });

  const [searchTerm, setSearchTerm] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [showRecommendations, setShowRecommendations] = useState(false);

  // æ£€æŸ¥åŽç«¯å¥åº·çŠ¶æ€
  const checkHealth = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        const healthData = await response.json();
        setConnectionStatus('connected');
        setError(null);
        return healthData;
      } else {
        throw new Error(`Health check failed: ${response.status}`);
      }
    } catch (err) {
      setConnectionStatus('disconnected');
      setError(`Backend connection failed: ${err.message}`);
      throw err;
    }
  };

  // åŠ è½½æ­¥é“æ•°æ®
  const loadTrails = async (retryCount = 0) => {
    setDataLoading(true);
    setError(null);

    try {
      // æ£€æŸ¥åŽç«¯å¥åº·çŠ¶æ€
      await checkHealth();

      // èŽ·å–æ­¥é“æ•°æ®
      const response = await fetch(`${API_BASE_URL}/trails`);

      if (!response.ok) {
        throw new Error(`Failed to fetch trails: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();

      // éªŒè¯å’Œæ¸…ç†æ•°æ®
      const validTrails = data.filter(trail => {
        return trail && 
               trail.trail_id && 
               trail.trail_name && 
               typeof trail.distance_km === 'number' &&
               !isNaN(trail.distance_km) &&
               trail.scenery_types;
      }).map(trail => ({
        ...trail,
        // ç¡®ä¿scenery_typesæ˜¯æ•°ç»„
        scenery_types: Array.isArray(trail.scenery_types) 
          ? trail.scenery_types 
          : (typeof trail.scenery_types === 'string' 
              ? trail.scenery_types.split(',').map(s => s.trim()) 
              : [])
      }));

      setTrails(validTrails);
      setFilteredTrails(validTrails);
      setConnectionStatus('connected');

      console.log(`âœ… Successfully loaded ${validTrails.length} trails`);

    } catch (error) {
      console.error('âŒ Error loading trails:', error);
      setError(error.message);

      // å¦‚æžœæ˜¯ç¬¬ä¸€æ¬¡å°è¯•ä¸”é”™è¯¯å¯èƒ½æ˜¯ä¸´æ—¶çš„ï¼Œé‡è¯•ä¸€æ¬¡
      if (retryCount === 0 && error.message.includes('fetch')) {
        console.log('ðŸ”„ Retrying in 2 seconds...');
        setTimeout(() => loadTrails(1), 2000);
        return;
      }

      // ä½¿ç”¨ç¤ºä¾‹æ•°æ®ä½œä¸ºåŽå¤‡
      const fallbackTrails = [
        {
          trail_id: "SAMPLE_001",
          trail_name: "Stanley Park Seawall",
          location: "Vancouver, BC",
          difficulty: "Easy",
          distance_km: 8.9,
          estimated_time_hours: 2.5,
          elevation_gain_m: 50,
          rating: 4.5,
          scenery_types: ["Ocean views", "City skyline", "Parks"],
          trail_type: "Loop",
          description: "A paved pathway around Stanley Park offering stunning ocean and city views."
        },
        {
          trail_id: "SAMPLE_002",
          trail_name: "Quarry Rock",
          location: "North Vancouver, BC",
          difficulty: "Easy",
          distance_km: 3.8,
          estimated_time_hours: 1.2,
          elevation_gain_m: 100,
          rating: 4.6,
          scenery_types: ["Ocean views", "Forest"],
          trail_type: "Out & Back",
          description: "A popular short hike to a scenic viewpoint overlooking Deep Cove."
        }
      ];

      setTrails(fallbackTrails);
      setFilteredTrails(fallbackTrails);
      setConnectionStatus('disconnected');
    } finally {
      setDataLoading(false);
    }
  };

  // åˆå§‹åŒ–åŠ è½½
  useEffect(() => {
    loadTrails();
  }, []);

  // èŽ·å–æ‰€æœ‰ç‹¬ç‰¹çš„æ™¯è§‚ç±»åž‹
  const getAllSceneryTypes = () => {
    try {
      const allTypes = trails.flatMap(trail => 
        Array.isArray(trail.scenery_types) ? trail.scenery_types : []
      );
      return [...new Set(allTypes)].filter(type => type && type.trim().length > 0).sort();
    } catch (error) {
      console.error('Error getting scenery types:', error);
      return [];
    }
  };

  // åº”ç”¨ç­›é€‰æ¡ä»¶
  useEffect(() => {
    if (!trails.length) return;

    try {
      const filtered = trails.filter(trail => {
        // æœç´¢è¯ç­›é€‰
        if (searchTerm) {
          const searchLower = searchTerm.toLowerCase();
          if (!trail.trail_name.toLowerCase().includes(searchLower) &&
              !trail.location.toLowerCase().includes(searchLower)) {
            return false;
          }
        }

        // å…¶ä»–ç­›é€‰æ¡ä»¶...
        if (filters.difficulty && trail.difficulty !== filters.difficulty) return false;
        if (filters.maxDistance && trail.distance_km > parseFloat(filters.maxDistance)) return false;
        if (filters.maxTime && trail.estimated_time_hours > parseFloat(filters.maxTime)) return false;
        if (filters.maxElevation && trail.elevation_gain_m > parseInt(filters.maxElevation)) return false;
        if (filters.trailType && trail.trail_type !== filters.trailType) return false;
        if (filters.minRating && trail.rating < parseFloat(filters.minRating)) return false;

        // æ™¯è§‚ç±»åž‹ç­›é€‰
        if (filters.sceneryTypes.length > 0) {
          const hasMatchingScenery = filters.sceneryTypes.some(selectedType =>
            Array.isArray(trail.scenery_types) && 
            trail.scenery_types.some(trailType => 
              trailType.toLowerCase().includes(selectedType.toLowerCase())
            )
          );
          if (!hasMatchingScenery) return false;
        }

        return true;
      });

      setFilteredTrails(filtered);
    } catch (error) {
      console.error('Error filtering trails:', error);
    }
  }, [trails, filters, searchTerm]);

  // å¤„ç†ç­›é€‰æ¡ä»¶å˜åŒ–
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // å¤„ç†æ™¯è§‚ç±»åž‹é€‰æ‹©
  const handleSceneryTypeToggle = (sceneryType) => {
    setFilters(prev => ({
      ...prev,
      sceneryTypes: prev.sceneryTypes.includes(sceneryType)
        ? prev.sceneryTypes.filter(type => type !== sceneryType)
        : [...prev.sceneryTypes, sceneryType]
    }));
  };

  // æ·»åŠ æ­¥é“åˆ°é€‰æ‹©åˆ—è¡¨
  const handleTrailSelect = (trail) => {
    if (selectedTrails.find(t => t.trail_id === trail.trail_id)) {
      setSelectedTrails(prev => prev.filter(t => t.trail_id !== trail.trail_id));
    } else {
      setSelectedTrails(prev => [...prev, trail]);
    }
  };

  // è®¡ç®—ç¾¤ä½“åŒ¹é…åº¦
  const calculateGroupMatch = (trail) => {
    try {
      if (!Array.isArray(trail.scenery_types)) return 0;

      let matchCount = 0;
      groupMembers.forEach(member => {
        const memberMatch = member.preferences.some(pref =>
          trail.scenery_types.some(scenery => 
            scenery.toLowerCase().includes(pref.toLowerCase())
          )
        );
        if (memberMatch) matchCount++;
      });
      return (matchCount / groupMembers.length) * 100;
    } catch (error) {
      return 0;
    }
  };

  // èŽ·å–æŽ¨èæ­¥é“
  const getRecommendations = async () => {
    if (connectionStatus !== 'connected') {
      setError('Please ensure backend is connected before getting recommendations');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/trails/recommend`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          groupPreferences: groupMembers,
          filters: filters,
          k: 5
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      if (!data.recommendations) {
        throw new Error('Invalid response format: missing recommendations');
      }

      setRecommendations(data.recommendations);
      setRecommendationMetrics(data.metrics);
      setShowRecommendations(true);

      console.log('âœ… Recommendations received:', data);

    } catch (error) {
      console.error('âŒ Recommendation error:', error);
      setError(`Recommendation failed: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // æ¸…ç©ºç­›é€‰æ¡ä»¶
  const clearFilters = () => {
    setFilters({
      difficulty: '',
      maxDistance: '',
      maxTime: '',
      maxElevation: '',
      trailType: '',
      sceneryTypes: [],
      minRating: ''
    });
    setSearchTerm('');
    setError(null);
  };

  // å¦‚æžœæ•°æ®æ­£åœ¨åŠ è½½ï¼Œæ˜¾ç¤ºåŠ è½½çŠ¶æ€
  if (dataLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <RefreshCw className="animate-spin mx-auto mb-4 text-blue-600" size={48} />
          <h2 className="text-xl font-semibold text-gray-800">Loading Trail Data...</h2>
          <p className="text-gray-600">Connecting to backend and loading trails</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      {/* é”™è¯¯æç¤º */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <AlertCircle className="text-red-500" size={20} />
              <div>
                <h3 className="font-medium text-red-800">Connection Issue</h3>
                <p className="text-sm text-red-600">{error}</p>
              </div>
            </div>
            <button 
              onClick={() => loadTrails()} 
              className="px-3 py-1 bg-red-100 text-red-700 rounded hover:bg-red-200 transition-colors"
            >
              Retry
            </button>
          </div>
        </div>
      )}

      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        {/* æ ‡é¢˜å’ŒçŠ¶æ€ */}
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-3xl font-bold text-gray-800 flex items-center gap-2">
            <Mountain className="text-blue-600" />
            Feature-Based Trail Selection System
          </h1>
          <div className="flex items-center gap-2">
            <div className={`w-3 h-3 rounded-full ${
              connectionStatus === 'connected' ? 'bg-green-500' : 
              connectionStatus === 'disconnected' ? 'bg-red-500' : 'bg-yellow-500'
            }`}></div>
            <span className="text-sm text-gray-600 capitalize">{connectionStatus}</span>
            <span className="text-sm text-blue-600">({trails.length} trails)</span>
          </div>
        </div>

        <p className="text-gray-600 mb-4">
          AI-powered trail selection using Greedy MinMax Regret algorithm for optimal group satisfaction
        </p>

        {/* ç¾¤ä½“æˆå‘˜æ˜¾ç¤º */}
        <div className="bg-blue-50 rounded-lg p-4 mb-6">
          <h3 className="text-lg font-semibold text-blue-800 mb-2 flex items-center gap-2">
            <Users size={20} />
            Group Member Preferences
          </h3>
