                    <div className="text-sm text-blue-700 space-y-1">
                      <p>Average Rating: {(recommendationMetrics.averageRating || 0).toFixed(1)}/5.0</p>
                      <p>Average Distance: {(recommendationMetrics.averageDistance || 0).toFixed(1)}km</p>
                      <p>Average Time: {(recommendationMetrics.averageTime || 0).toFixed(1)} hours</p>
                      <p>Average Elevation: {(recommendationMetrics.averageElevation || 0).toFixed(0)}m</p>
                    </div>
                  </div>

                  <div className="bg-green-50 rounded-lg p-3">
                    <h4 className="font-medium text-green-800 mb-2">Algorithm Performance</h4>
                    <div className="text-sm text-green-700 space-y-1">
                      <p>Diversity Score: {((recommendationMetrics.diversityScore || 0) * 100).toFixed(0)}%</p>
                      <p>Regret Score: {(recommendationMetrics.regretScore || 0).toFixed(2)}</p>
                    </div>
                  </div>

                  <div className="bg-purple-50 rounded-lg p-3">
                    <h4 className="font-medium text-purple-800 mb-2">Group Satisfaction</h4>
                    <div className="text-sm text-purple-700 space-y-1">
                      {groupMembers.map((member, index) => (
                        <p key={member.name}>
                          {member.name}: {(recommendationMetrics.groupMatchPercentages && recommendationMetrics.groupMatchPercentages[index]) || 0}% match
                        </p>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* æ­¥é“åˆ—è¡¨ */}
