                <h4 className="font-semibold text-blue-800 mb-2">Selection Summary</h4>
                <div className="text-sm text-blue-700">
                  <p>Total Distance: {selectedTrails.reduce((sum, trail) => sum + trail.distance_km, 0).toFixed(1)}km</p>
                  <p>Total Time: {selectedTrails.reduce((sum, trail) => sum + trail.estimated_time_hours, 0).toFixed(1)} hours</p>
                  <p>Average Rating: {selectedTrails.length > 0 ? (selectedTrails.reduce((sum, trail) => sum + trail.rating, 0) / selectedTrails.length).toFixed(1) : 0}/5.0</p>
                  <p>Average Group Match: {selectedTrails.length > 0 ? (selectedTrails.reduce((sum, trail) => sum + calculateGroupMatch(trail), 0) / selectedTrails.length).toFixed(0) : 0}%</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default InteractiveTrailSelector;
