import React, { useState, useEffect } from 'react';
import { Search, Filter, MapPin, Clock, Mountain, Star, Users, Eye, Zap, TrendingUp, AlertCircle, RefreshCw, Heart, Activity, Award, ChevronDown, X, Plus, User, Settings, ArrowRight, ArrowLeft, Home } from 'lucide-react';

const InteractiveTrailSelector = () => {
  const [trails, setTrails] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dataLoading, setDataLoading] = useState(false);
  const [error, setError] = useState(null);

  // Group management
  const [groupSize, setGroupSize] = useState('');
  const [groupMembers, setGroupMembers] = useState([]);
  const [currentStep, setCurrentStep] = useState('group-size'); // 'group-size', 'preferences', 'results'
  const [currentMemberIndex, setCurrentMemberIndex] = useState(0);

  // Available preferences
  const availablePreferences = [
    { id: 'distance', label: 'Distance', icon: MapPin, options: ['Short (< 5km)', 'Medium (5-10km)', 'Long (> 10km)'] },
    { id: 'elevation', label: 'Elevation Gain', icon: Mountain, options: ['Low (< 200m)', 'Medium (200-500m)', 'High (> 500m)'] },
    { id: 'time', label: 'Duration', icon: Clock, options: ['Quick (< 2h)', 'Moderate (2-4h)', 'Long (> 4h)'] },
    { id: 'difficulty', label: 'Difficulty', icon: Activity, options: ['Easy', 'Moderate', 'Hard'] },
    { id: 'scenery', label: 'Scenery Type', icon: Eye, options: ['Mountain', 'Lake', 'Forest', 'Ocean', 'Waterfall', 'Alpine'] }
  ];

  const [filters, setFilters] = useState({
    difficulty: '',
    maxDistance: '',
    maxTime: '',
    minRating: ''
  });

  const [searchTerm, setSearchTerm] = useState('');

  // Load trail data from backend
  useEffect(() => {
    const loadTrails = async () => {
      setDataLoading(true);
      setError(null);

      try {
        console.log('ðŸ” Attempting to fetch trails from:', 'http://localhost:3001/api/trails');

        const response = await fetch('http://localhost:3001/api/trails');

        console.log('ðŸ“¡ Response status:', response.status);
        console.log('ðŸ“¡ Response headers:', response.headers);

        if (!response.ok) {
          const errorText = await response.text();
          console.error('âŒ HTTP error response:', errorText);
          throw new Error(`HTTP error! status: ${response.status} - ${errorText}`);
        }

        const data = await response.json();
        console.log('âœ… Received data:', data.length, 'trails');

        // Process data format, ensure scenery_types is array
        const processedData = data.map(trail => ({
          ...trail,
          distance_km: parseFloat(trail.distance_km),
          estimated_time_hours: parseFloat(trail.estimated_time_hours),
          elevation_gain_m: parseInt(trail.elevation_gain_m),
          rating: parseFloat(trail.rating),
          scenery_types: Array.isArray(trail.scenery_types) 
            ? trail.scenery_types 
            : (typeof trail.scenery_types === 'string' 
                ? trail.scenery_types.split(',').map(s => s.trim()) 
                : [])
        }));

        setTrails(processedData);
        console.log(`âœ… Successfully loaded ${processedData.length} trail data`);
      } catch (err) {
        console.error('âŒ Error loading trails:', err);
        console.error('âŒ Error details:', {
          name: err.name,
          message: err.message,
          stack: err.stack
        });
        setError(`Failed to load trails: ${err.message}`);
      } finally {
        setDataLoading(false);
      }
    };

    loadTrails();
  }, []);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const initializeGroupMembers = (size) => {
    const members = [];
    for (let i = 0; i < size; i++) {
      members.push({
        id: i,
        name: `Person ${i + 1}`,
        preferences: {}
      });
    }
    setGroupMembers(members);
    setCurrentStep('preferences');
    setCurrentMemberIndex(0);
  };

  const updateMemberPreference = (memberId, preferenceType, value) => {
    setGroupMembers(members => 
      members.map(member => 
        member.id === memberId 
          ? { ...member, preferences: { ...member.preferences, [preferenceType]: value } }
          : member
      )
    );
  };

  const nextMember = () => {
    if (currentMemberIndex < groupMembers.length - 1) {
      setCurrentMemberIndex(currentMemberIndex + 1);
    } else {
      // All members have set preferences, get recommendations
      getRecommendations();
    }
  };

  const prevMember = () => {
    if (currentMemberIndex > 0) {
      setCurrentMemberIndex(currentMemberIndex - 1);
    }
  };

  const calculateGroupMatch = (trail) => {
    if (groupMembers.length === 0) return 0;
    let matchCount = 0;
    groupMembers.forEach(member => {
      const memberMatch = member.preferences.scenery && 
        trail.scenery_types.some(scenery => 
          scenery.toLowerCase().includes(member.preferences.scenery.toLowerCase())
        );
      if (memberMatch) matchCount++;
    });
    return (matchCount / groupMembers.length) * 100;
  };

  const getRecommendations = async () => {
    setLoading(true);
    setError(null);

    try {
      // Convert groupMembers to the format expected by the backend
      const groupPreferences = groupMembers.map(member => ({
        name: member.name,
        preferences: Object.keys(member.preferences).map(key => {
          const value = member.preferences[key];
          // Convert preference values to match backend expectations
          switch(key) {
            case 'distance':
              if (value === 'Short (< 5km)') return 'short';
              if (value === 'Medium (5-10km)') return 'medium';
              if (value === 'Long (> 10km)') return 'long';
              return value;
            case 'elevation':
              if (value === 'Low (< 200m)') return 'low';
              if (value === 'Medium (200-500m)') return 'medium';
              if (value === 'High (> 500m)') return 'high';
              return value;
            case 'time':
              if (value === 'Quick (< 2h)') return 'quick';
              if (value === 'Moderate (2-4h)') return 'moderate';
              if (value === 'Long (> 4h)') return 'long';
              return value;
            case 'difficulty':
              return value.toLowerCase();
            case 'scenery':
              return value.toLowerCase();
            default:
              return value;
          }
        }).filter(pref => pref) // Remove empty preferences
      }));

      console.log('Sending group preferences:', groupPreferences);

      const response = await fetch('http://localhost:3001/api/trails/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          groupPreferences: groupPreferences,
          filters: filters,
          k: 5
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(`Recommendation request failed: ${response.status} - ${errorData.error || 'Unknown error'}`);
      }

      const data = await response.json();

      // Add group match information to recommended trails
      const recommendationsWithGroupMatch = data.recommendations.map(trail => ({
        ...trail,
        groupMatchPercentage: calculateGroupMatch(trail)
      }));

      setRecommendations(recommendationsWithGroupMatch);
      setCurrentStep('results');

      console.log('âœ… Recommendation algorithm completed:', {
        algorithm: data.algorithm,
        totalCandidates: data.totalCandidates,
        recommendationsCount: data.recommendations.length
      });

    } catch (error) {
      console.error('âŒ Recommendation algorithm error:', error);
      setError(`Recommendation failed: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const resetToGroupSize = () => {
    setCurrentStep('group-size');
    setGroupSize('');
    setGroupMembers([]);
    setCurrentMemberIndex(0);
    setRecommendations([]);
  };

  if (dataLoading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <div className="w-20 h-20 bg-blue-600 rounded-full flex items-center justify-center mb-8 mx-auto">
            <Mountain className="text-white" size={40} />
          </div>
          <h2 className="text-3xl font-semibold text-gray-800 mb-4">Loading Trail Data...</h2>
          <p className="text-xl text-gray-600">Connecting to backend and loading trails</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <header className="border-b border-gray-200">
          <div className="max-w-6xl mx-auto px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
                  <Home className="text-white" size={20} />
                </div>
                <h1 className="text-3xl font-bold text-gray-900">Trail Explorer</h1>
              </div>
              <div className="flex items-center gap-6">
                <a href="#" className="text-lg text-gray-600 hover:text-gray-900">About</a>
                <a href="#" className="text-lg text-gray-600 hover:text-gray-900">Help</a>
                <button className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-lg font-medium">
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </header>

        {/* Error Alert */}
        {error && (
          <div className="max-w-6xl mx-auto px-6 py-4">
            <div className="bg-red-50 border border-red-200 rounded-lg p-6">
              <div className="flex items-center gap-3">
                <AlertCircle className="text-red-500" size={24} />
                <span className="text-lg text-red-700">{error}</span>
              </div>
            </div>
          </div>
        )}

        {/* Main Content */}
        <main className="max-w-6xl mx-auto px-6 py-12">
          {/* Step 1: Group Size Selection */}
          {currentStep === 'group-size' && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
              {/* Left Column - Image */}
              <div className="relative">
                <div className="bg-gradient-to-br from-blue-50 to-indigo-100 rounded-2xl p-16 text-center">
                  <div className="w-32 h-32 bg-blue-600 rounded-full flex items-center justify-center mx-auto mb-8">
                    <Users className="text-white" size={64} />
                  </div>
                  <h2 className="text-4xl font-bold text-gray-900 mb-6">
                    Find your <span className="text-blue-600">perfect trail</span>
                  </h2>
                  <p className="text-xl text-gray-600 leading-relaxed">
                    AI-powered trail recommendations for groups. 
                    We connect people, analyze preferences, and find the best hiking adventures.
                  </p>
                </div>
              </div>

              {/* Right Column - Form */}
              <div className="space-y-8">
                <div>
                  <h3 className="text-3xl font-semibold text-gray-900 mb-4">How many people in your group?</h3>
                  <p className="text-lg text-gray-600 mb-8">Select the number of people in your hiking group</p>

                  <div className="space-y-4">
                    <label className="block text-lg font-medium text-gray-700 mb-3">
                      Group Size
                    </label>
                    <select
                      value={groupSize}
                      onChange={(e) => {
                        const size = parseInt(e.target.value);
                        if (size > 0) {
                          initializeGroupMembers(size);
                        }
                      }}
                      className="w-full p-6 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg text-gray-900"
                    >
                      <option value="">Select group size...</option>
                      {[1, 2, 3, 4, 5, 6, 7, 8].map((size) => (
                        <option key={size} value={size}>
                          {size} {size === 1 ? 'Person' : 'People'}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div className="bg-gray-50 rounded-lg p-8">
                  <h4 className="text-xl font-semibold text-gray-900 mb-4">What happens next?</h4>
                  <ul className="space-y-3 text-lg text-gray-600">
                    <li className="flex items-center gap-3">
                      <div className="w-3 h-3 bg-blue-600 rounded-full"></div>
                      Each person will set their preferences
                    </li>
                    <li className="flex items-center gap-3">
                      <div className="w-3 h-3 bg-blue-600 rounded-full"></div>
                      Our AI analyzes group compatibility
                    </li>
                    <li className="flex items-center gap-2">
                      <div className="w-2 h-2 bg-blue-600 rounded-full"></div>
                      Get top 5 personalized recommendations
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Individual Preferences */}
          {currentStep === 'preferences' && (
            <div className="max-w-4xl mx-auto">
              <div className="text-center mb-12">
                <h2 className="text-4xl font-bold text-gray-900 mb-4">
                  {groupMembers[currentMemberIndex]?.name}'s Preferences
                </h2>
                <p className="text-xl text-gray-600">
                  Member {currentMemberIndex + 1} of {groupMembers.length}
                </p>
              </div>

              <div className="space-y-8">
                {availablePreferences.map((pref) => (
                  <div key={pref.id} className="bg-white rounded-xl shadow-lg p-8">
                    <div className="flex items-center gap-4 mb-6">
                      <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                        {pref.icon}
                      </div>
                      <div>
                        <h3 className="text-2xl font-semibold text-gray-900">{pref.label}</h3>
                        <p className="text-lg text-gray-600">Select your preferred {pref.label.toLowerCase()}</p>
                      </div>
                    </div>

                    <select
                      value={groupMembers[currentMemberIndex]?.preferences[pref.id] || ''}
                      onChange={(e) => updateMemberPreference(currentMemberIndex, pref.id, e.target.value)}
                      className="w-full p-6 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg text-gray-900"
                    >
                      <option value="">Select {pref.label.toLowerCase()}...</option>
                      {pref.options.map((option) => (
                        <option key={option} value={option}>
                          {option}
                        </option>
                      ))}
                    </select>
                  </div>
                ))}
              </div>

              <div className="flex justify-between items-center mt-12">
                <button
                  onClick={prevMember}
                  disabled={currentMemberIndex === 0}
                  className="px-8 py-4 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed text-lg font-medium"
                >
                  Previous
                </button>

                {currentMemberIndex === groupMembers.length - 1 ? (
                  <button
                    onClick={getRecommendations}
                    disabled={loading}
                    className="px-8 py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed text-lg font-medium"
                  >
                    {loading ? 'Getting Recommendations...' : 'Get Recommendations'}
                  </button>
                ) : (
                  <button
                    onClick={nextMember}
                    className="px-8 py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-lg font-medium"
                  >
                    Next Member
                  </button>
                )}
              </div>
            </div>
          )}

          {/* Step 3: Results */}
          {currentStep === 'results' && (
            <div className="max-w-6xl mx-auto">
              <div className="text-center mb-12">
                <h2 className="text-4xl font-bold text-gray-900 mb-4">Your Recommended Trails</h2>
                <p className="text-xl text-gray-600">
                  Top 5 trails selected for your group of {groupMembers.length} people
                </p>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                {recommendations.map((trail, index) => (
                  <div key={trail.trail_id} className="bg-white rounded-xl shadow-lg p-8 border border-gray-200">
                    <div className="flex justify-between items-start mb-6">
                      <div>
                        <div className="flex items-center gap-3 mb-3">
                          <div className="w-8 h-8 bg-blue-600 text-white rounded-full flex items-center justify-center text-lg font-bold">
                            {index + 1}
                          </div>
                          <h3 className="text-2xl font-bold text-gray-900">{trail.trail_name}</h3>
                        </div>
                        <p className="text-lg text-gray-600 mb-4">{trail.location}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Star className="text-yellow-500" size={24} />
                        <span className="text-xl font-semibold text-gray-900">{trail.rating}</span>
                      </div>
                    </div>

                    <div className="grid grid-cols-2 gap-6 mb-6">
                      <div className="space-y-3">
                        <div className="flex items-center gap-3">
                          <MapPin className="text-gray-500" size={20} />
                          <span className="text-lg text-gray-700">{trail.distance_km}km</span>
                        </div>
                        <div className="flex items-center gap-3">
                          <Clock className="text-gray-500" size={20} />
                          <span className="text-lg text-gray-700">{trail.estimated_time_hours}h</span>
                        </div>
                      </div>
                      <div className="space-y-3">
                        <div className="flex items-center gap-3">
                          <Mountain className="text-gray-500" size={20} />
                          <span className="text-lg text-gray-700">{trail.elevation_gain_m}m</span>
                        </div>
                        <div className="flex items-center gap-3">
                          <Users className="text-gray-500" size={20} />
                          <span className="text-lg text-gray-700">{trail.groupMatchPercentage}% match</span>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center gap-3 mb-6">
                      <span className={`px-4 py-2 rounded-full text-lg font-medium ${
                        trail.difficulty === 'Easy' ? 'bg-green-100 text-green-800' :
                        trail.difficulty === 'Moderate' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {trail.difficulty}
                      </span>
                      <span className="px-4 py-2 bg-blue-100 text-blue-800 rounded-full text-lg font-medium">
                        {trail.trail_type}
                      </span>
                    </div>

                    <div className="flex flex-wrap gap-2 mb-6">
                      {trail.scenery_types.map((scenery, i) => (
                        <span key={i} className="px-3 py-2 bg-gray-100 text-gray-700 rounded-full text-lg">
                          {scenery}
                        </span>
                      ))}
                    </div>

                    <p className="text-lg text-gray-600 leading-relaxed">
                      {trail.description}
                    </p>
                  </div>
                ))}
              </div>

              <div className="text-center mt-12">
                <button
                  onClick={resetToGroupSize}
                  className="px-8 py-4 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors text-lg font-medium"
                >
                  Start Over
                </button>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default InteractiveTrailSelector;
