import React, { useState, useEffect } from 'react';
import { Search, Filter, MapPin, Clock, Mountain, Star, Users, Eye, Zap, TrendingUp, AlertCircle, RefreshCw, Heart, Activity, Award, ChevronDown, X, Plus, User, Settings, ArrowRight, ArrowLeft } from 'lucide-react';

const InteractiveTrailSelector = () => {
  const [trails, setTrails] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dataLoading, setDataLoading] = useState(false);
  const [error, setError] = useState(null);

  // Group management
  const [groupSize, setGroupSize] = useState(0);
  const [groupMembers, setGroupMembers] = useState([]);
  const [currentStep, setCurrentStep] = useState('group-size'); // 'group-size', 'preferences', 'results'
  const [currentMemberIndex, setCurrentMemberIndex] = useState(0);

  // Available preferences
  const availablePreferences = [
    { id: 'distance', label: 'Distance', icon: MapPin, options: ['Short (< 5km)', 'Medium (5-10km)', 'Long (> 10km)'] },
    { id: 'elevation', label: 'Elevation Gain', icon: Mountain, options: ['Low (< 200m)', 'Medium (200-500m)', 'High (> 500m)'] },
    { id: 'time', label: 'Duration', icon: Clock, options: ['Quick (< 2h)', 'Moderate (2-4h)', 'Long (> 4h)'] },
    { id: 'difficulty', label: 'Difficulty', icon: Activity, options: ['Easy', 'Moderate', 'Hard'] },
    { id: 'scenery', label: 'Scenery Type', icon: Eye, options: ['Mountain', 'Lake', 'Forest', 'Ocean', 'Waterfall', 'Alpine'] }
  ];

  const [filters, setFilters] = useState({
    difficulty: '',
    maxDistance: '',
    maxTime: '',
    minRating: ''
  });

  const [searchTerm, setSearchTerm] = useState('');

  // Load trail data from backend
  useEffect(() => {
    const loadTrails = async () => {
      setDataLoading(true);
      setError(null);

      try {
        const response = await fetch('http://localhost:3001/api/trails');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        // Process data format, ensure scenery_types is array
        const processedData = data.map(trail => ({
          ...trail,
          distance_km: parseFloat(trail.distance_km),
          estimated_time_hours: parseFloat(trail.estimated_time_hours),
          elevation_gain_m: parseInt(trail.elevation_gain_m),
          rating: parseFloat(trail.rating),
          scenery_types: Array.isArray(trail.scenery_types) 
            ? trail.scenery_types 
            : (typeof trail.scenery_types === 'string' 
                ? trail.scenery_types.split(',').map(s => s.trim()) 
                : [])
        }));

        setTrails(processedData);
        console.log(`✅ Successfully loaded ${processedData.length} trail data`);
      } catch (err) {
        console.error('Error loading trails:', err);
        setError(`Failed to load trails: ${err.message}`);
      } finally {
        setDataLoading(false);
      }
    };

    loadTrails();
  }, []);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const initializeGroupMembers = (size) => {
    const members = [];
    for (let i = 0; i < size; i++) {
      members.push({
        id: i,
        name: `Person ${i + 1}`,
        preferences: {}
      });
    }
    setGroupMembers(members);
    setGroupSize(size);
    setCurrentStep('preferences');
    setCurrentMemberIndex(0);
  };

  const updateMemberPreference = (memberId, preferenceType, value) => {
    setGroupMembers(members => 
      members.map(member => 
        member.id === memberId 
          ? { ...member, preferences: { ...member.preferences, [preferenceType]: value } }
          : member
      )
    );
  };

  const nextMember = () => {
    if (currentMemberIndex < groupMembers.length - 1) {
      setCurrentMemberIndex(currentMemberIndex + 1);
    } else {
      // All members have set preferences, get recommendations
      getRecommendations();
    }
  };

  const prevMember = () => {
    if (currentMemberIndex > 0) {
      setCurrentMemberIndex(currentMemberIndex - 1);
    }
  };

  const calculateGroupMatch = (trail) => {
    if (groupMembers.length === 0) return 0;
    let matchCount = 0;
    groupMembers.forEach(member => {
      const memberMatch = member.preferences.scenery && 
        trail.scenery_types.some(scenery => 
          scenery.toLowerCase().includes(member.preferences.scenery.toLowerCase())
        );
      if (memberMatch) matchCount++;
    });
    return (matchCount / groupMembers.length) * 100;
  };

  const getRecommendations = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:3001/api/trails/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          groupPreferences: groupMembers,
          filters: filters,
          k: 5
        })
      });

      if (!response.ok) {
        throw new Error(`Recommendation request failed: ${response.status}`);
      }

      const data = await response.json();

      // Add group match information to recommended trails
      const recommendationsWithGroupMatch = data.recommendations.map(trail => ({
        ...trail,
        groupMatchPercentage: calculateGroupMatch(trail)
      }));

      setRecommendations(recommendationsWithGroupMatch);
      setCurrentStep('results');

      console.log('✅ Recommendation algorithm completed:', {
        algorithm: data.algorithm,
        totalCandidates: data.totalCandidates,
        recommendationsCount: data.recommendations.length
      });

    } catch (error) {
      console.error('❌ Recommendation algorithm error:', error);
      setError(`Recommendation failed: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const resetToGroupSize = () => {
    setCurrentStep('group-size');
    setGroupSize(0);
    setGroupMembers([]);
    setCurrentMemberIndex(0);
    setRecommendations([]);
  };

  if (dataLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 flex items-center justify-center">
        <div className="text-center">
          <div className="relative">
            <div className="w-20 h-20 bg-gradient-to-r from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center mb-6 mx-auto shadow-lg">
              <Mountain className="text-white animate-pulse" size={40} />
            </div>
            <RefreshCw className="absolute -top-2 -right-2 animate-spin text-blue-600" size={24} />
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Loading Trail Data...</h2>
          <p className="text-gray-600">Connecting to backend and loading trails</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <div className="max-w-4xl mx-auto p-6">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center shadow-lg">
              <Mountain className="text-white" size={32} />
            </div>
            <div>
              <h1 className="text-4xl font-bold text-gray-800 mb-2">Trail Explorer</h1>
              <p className="text-gray-600">AI-powered trail recommendations for groups</p>
            </div>
          </div>
        </div>

        {/* Error Alert */}
        {error && (
          <div className="bg-gradient-to-r from-red-50 to-pink-50 border border-red-100 rounded-2xl p-6 mb-8 shadow-sm">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 bg-red-100 rounded-full flex items-center justify-center">
                  <AlertCircle className="text-red-500" size={20} />
                </div>
                <div>
                  <h3 className="font-semibold text-red-800">Connection Issue</h3>
                  <p className="text-sm text-red-600">{error}</p>
                </div>
              </div>
              <button className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors shadow-sm">
                Retry
              </button>
            </div>
          </div>
        )}

        {/* Step 1: Group Size Selection */}
        {currentStep === 'group-size' && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-8 border border-white/20">
            <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <Users className="text-white" size={20} />
              </div>
              How many people in your group?
            </h2>

            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
              {[1, 2, 3, 4, 5, 6, 7, 8].map((size) => (
                <button
                  key={size}
                  onClick={() => initializeGroupMembers(size)}
                  className="bg-gradient-to-r from-blue-500 to-purple-600 text-white py-6 px-4 rounded-xl hover:from-blue-600 hover:to-purple-700 transition-all duration-200 flex flex-col items-center justify-center gap-2 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                >
                  <div className="text-3xl font-bold">{size}</div>
                  <div className="text-sm opacity-90">
                    {size === 1 ? 'Person' : 'People'}
                  </div>
                </button>
              ))}
            </div>

            <div className="text-center text-gray-600">
              <p>Select the number of people in your hiking group</p>
              <p className="text-sm mt-2">Each person will set their preferences next</p>
            </div>
          </div>
        )}

        {/* Step 2: Preferences Selection */}
        {currentStep === 'preferences' && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-8 border border-white/20">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-3">
                <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-emerald-600 rounded-lg flex items-center justify-center">
                  <Settings className="text-white" size={20} />
                </div>
                {groupMembers[currentMemberIndex]?.name}'s Preferences
              </h2>
              <div className="text-sm text-gray-600">
                {currentMemberIndex + 1} of {groupMembers.length}
              </div>
            </div>

            <div className="space-y-6">
              {availablePreferences.map((pref) => {
                const Icon = pref.icon;
                const currentValue = groupMembers[currentMemberIndex]?.preferences[pref.id] || '';

                return (
                  <div key={pref.id} className="bg-gradient-to-br from-gray-50 to-white rounded-xl p-6 border border-gray-200">
                    <div className="flex items-center gap-3 mb-4">
                      <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                        <Icon className="text-blue-600" size={20} />
                      </div>
                      <h3 className="font-semibold text-gray-800 text-lg">{pref.label}</h3>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                      {pref.options.map((option) => (
                        <button
                          key={option}
                          onClick={() => updateMemberPreference(groupMembers[currentMemberIndex].id, pref.id, option)}
                          className={`p-4 rounded-lg text-sm font-medium transition-all duration-200 ${
                            currentValue === option
                              ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg transform scale-105'
                              : 'bg-white text-gray-700 hover:bg-gray-50 border border-gray-200 shadow-sm hover:shadow-md'
                          }`}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="flex justify-between items-center mt-8">
              <button
                onClick={prevMember}
                disabled={currentMemberIndex === 0}
                className="flex items-center gap-2 px-6 py-3 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <ArrowLeft size={16} />
                Previous Person
              </button>

              <button
                onClick={nextMember}
                disabled={loading}
                className="flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              >
                {loading ? (
                  <RefreshCw className="animate-spin" size={16} />
                ) : (
                  <>
                    {currentMemberIndex === groupMembers.length - 1 ? 'Get Recommendations' : 'Next Person'}
                    <ArrowRight size={16} />
                  </>
                )}
              </button>
            </div>
          </div>
        )}

        {/* Step 3: Results */}
        {currentStep === 'results' && (
          <div className="space-y-6">
            {/* Top 5 Recommendations */}
            {recommendations.length > 0 && (
              <div className="bg-gradient-to-br from-green-50 to-emerald-50 rounded-2xl shadow-lg p-8 border border-green-100">
                <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">
                  <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-emerald-600 rounded-lg flex items-center justify-center">
                    <TrendingUp className="text-white" size={20} />
                  </div>
                  Top 5 Recommended Trails
                </h2>

                <div className="space-y-4">
                  {recommendations.map((trail, index) => (
                    <div key={trail.trail_id} className="bg-white/80 backdrop-blur-sm border border-green-200 rounded-xl p-6 shadow-md hover:shadow-lg transition-all duration-200">
                      <div className="flex justify-between items-start mb-3">
                        <div>
                          <div className="flex items-center gap-2 mb-1">
                            <span className="w-6 h-6 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-full flex items-center justify-center text-xs font-bold">
                              {index + 1}
                            </span>
                            <h4 className="font-bold text-gray-800 text-lg">{trail.trail_name}</h4>
                          </div>
                          <p className="text-sm text-gray-600 flex items-center gap-1">
                            <MapPin size={14} />
                            {trail.location}
                          </p>
                        </div>
                        <div className="flex items-center gap-1 bg-yellow-100 px-2 py-1 rounded-lg">
                          <Star className="text-yellow-500" size={16} />
                          <span className="text-sm font-semibold text-gray-700">{trail.rating}</span>
                        </div>
                      </div>

                      <div className="grid grid-cols-3 gap-4 mb-4">
                        <div className="text-center">
                          <div className="text-lg font-bold text-gray-800">{trail.distance_km}km</div>
                          <div className="text-xs text-gray-500">Distance</div>
                        </div>
                        <div className="text-center">
                          <div className="text-lg font-bold text-gray-800">{trail.estimated_time_hours}h</div>
                          <div className="text-xs text-gray-500">Duration</div>
                        </div>
                        <div className="text-center">
                          <div className="text-lg font-bold text-gray-800">{trail.elevation_gain_m}m</div>
                          <div className="text-xs text-gray-500">Elevation</div>
                        </div>
                      </div>

                      <div className="flex items-center gap-2 mb-4">
                        <span className={`px-3 py-1 rounded-full text-sm font-medium shadow-sm ${
                          trail.difficulty === 'Easy' ? 'bg-green-100 text-green-800' :
                          trail.difficulty === 'Moderate' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {trail.difficulty}
                        </span>
                        <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-medium shadow-sm">
                          {trail.trail_type}
                        </span>
                      </div>

                      <div className="flex flex-wrap gap-1 mb-3">
                        {trail.scenery_types.slice(0, 4).map((scenery, i) => (
                          <span key={i} className="px-2 py-1 bg-green-100 text-green-800 rounded-full text-xs font-medium">
                            {scenery}
                          </span>
                        ))}
                        {trail.scenery_types.length > 4 && (
                          <span className="px-2 py-1 bg-gray-200 text-gray-600 rounded-full text-xs font-medium">
                            +{trail.scenery_types.length - 4} more
                          </span>
                        )}
                      </div>

                      <div className="flex justify-between items-center text-sm">
                        <span className="text-gray-600">
                          Group Match: <span className="font-semibold text-green-600">{calculateGroupMatch(trail).toFixed(0)}%</span>
                        </span>
                        <span className="text-gray-600">
                          Utility: <span className="font-semibold text-blue-600">{((trail.averageMemberUtility || 0.8) * 100).toFixed(0)}%</span>
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Navigation */}
            <div className="flex justify-center gap-4">
              <button
                onClick={resetToGroupSize}
                className="px-6 py-3 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
              >
                Start Over
              </button>

              <button
                onClick={() => setCurrentStep('preferences')}
                className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
              >
                Edit Preferences
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InteractiveTrailSelector;
